import React, { useState, useEffect } from "react";
import { api } from "./api";

export default function NoticeBoard({ token }) {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);

  // Ï†ÑÏ≤¥ Í≥µÏßÄ Î∂àÎü¨Ïò§Í∏∞
  async function load() {
    try {
      const data = await api("/notices", {
        method: "GET",
        token,
      });
      setItems(data);
    } catch (err) {
      console.error("Í≥µÏßÄÏÇ¨Ìï≠ Î°úÎìú Ïã§Ìå®:", err);
    }
  }

  // ÌòÑÏû¨ Î°úÍ∑∏Ïù∏ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
  async function loadUser() {
    try {
      const data = await api("/me", { method: "GET", token });
      setUser(data);
    } catch (err) {
      console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
    }
  }

  useEffect(() => {
    load();
    loadUser();
    // eslint-disable-next-line
  }, []);

  // Í≥µÏßÄ ÏÇ≠Ï†ú
  async function deleteNotice(id) {
    if (!window.confirm("Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;
    try {
      await api(`/notices/${id}`, { method: "DELETE", token });
      await load();
    } catch (err) {
      alert("ÏÇ≠Ï†ú Ïã§Ìå®: " + err.message);
    }
  }

  // Í≥µÏßÄ ÏàòÏ†ï
  async function editNotice(id, oldTitle, oldBody) {
    const newTitle = prompt("ÏÉà Ï†úÎ™©:", oldTitle);
    const newBody = prompt("ÏÉà ÎÇ¥Ïö©:", oldBody);
    if (!newTitle || !newBody) return;
    try {
      await api(`/notices/${id}`, {
        method: "PUT",
        token,
        body: { title: newTitle, body: newBody },
      });
      await load();
    } catch (err) {
      alert("ÏàòÏ†ï Ïã§Ìå®: " + err.message);
    }
  }

  return (
    <div>
      <h2>üì¢ Í≥µÏßÄÏÇ¨Ìï≠ Î™©Î°ù</h2>
      {items.length === 0 ? (
        <p>Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {items.map((n) => {
            const canEdit =
              user &&
              (user.role === "admin" || user.username === n.username);

            return (
              <li key={n.id} style={{ marginBottom: "20px" }}>
                <h4>{n.title}</h4>
                <p>{n.body}</p>
                <small>
                  ÏûëÏÑ±Ïûê: {n.username} / Î≤îÏúÑ: {n.scope} / ÏûëÏÑ±Ïùº:{" "}
                  {new Date(n.created_at).toLocaleString()}
                </small>
                {canEdit && (
                  <div style={{ marginTop: "8px" }}>
                    <button
                      onClick={() =>
                        editNotice(n.id, n.title, n.body)
                      }
                    >
                      ‚úè ÏàòÏ†ï
                    </button>
                    <button
                      style={{ marginLeft: "8px", color: "red" }}
                      onClick={() => deleteNotice(n.id)}
                    >
                      üóë ÏÇ≠Ï†ú
                    </button>
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
