import React, { useState } from "react";
import { useNavigate } from "react-router-dom";  // ‚úÖ Ï∂îÍ∞Ä
import { api } from "./api";

export default function NoticeList({ token }) {
  const [items, setItems] = useState([]); 
  const [q, setQ] = useState("");
  const navigate = useNavigate();  // ‚úÖ ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏö© hook

  // Í≤ÄÏÉâ Ïã§Ìñâ Ìï®Ïàò
  const search = async () => { 
    if (!q.trim()) return; // Îπà Í≤ÄÏÉâÏñ¥ Î∞©ÏßÄ
    const data = await api(`/notices/search?q=${encodeURIComponent(q)}`, {
      method: "GET",
      token
    });
    setItems(data);
  };

  // ÏóîÌÑ∞ÌÇ§ ÏûÖÎ†• Ïãú Í≤ÄÏÉâ Ïã§Ìñâ
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault(); // ÏóîÌÑ∞Î°ú Ìèº submit Î∞©ÏßÄ
      search();
    }
  };

  return (
    <div>
      <h3>Í≥µÏßÄÏÇ¨Ìï≠</h3>
      <input
        value={q}
        onChange={e => setQ(e.target.value)}
        onKeyPress={handleKeyPress}   // üîë ÏóîÌÑ∞ÌÇ§ Ïù¥Î≤§Ìä∏ Ï∂îÍ∞Ä
        placeholder="Í≤ÄÏÉâÏñ¥ ÏûÖÎ†•"
      />
      <button onClick={search}>Í≤ÄÏÉâ</button>

      {/* üìÖ Ï∫òÎ¶∞Îçî Ïù¥Îèô Î≤ÑÌäº */}
      <button
        style={{ marginLeft: "10px", backgroundColor: "#4CAF50", color: "white" }}
        onClick={() => navigate("/calendar")}
      >
        üìÖ Ï∫òÎ¶∞Îçî Î≥¥Í∏∞
      </button>

      <div style={{ marginTop: "20px" }}>
        {items.map(n =>
          <div key={n.id} style={{ marginBottom: "15px" }}>
            <b>{n.title}</b>
            <p>{n.body}</p>
            <small>ÏûëÏÑ±Ïûê: {n.username} [{n.scope}]</small>
          </div>
        )}
      </div>
    </div>
  );
}
