// src/NoticeSearch.js
import React, { useState } from "react";
import { api } from "./api";

export default function NoticeSearch({ token }) {
  const [q, setQ] = useState("");
  const [results, setResults] = useState([]);

  // Í≤ÄÏÉâ Ïã§Ìñâ
  async function search() {
    if (!q.trim()) return;
    try {
      const data = await api(`/notices/search?q=${encodeURIComponent(q)}`, {
        method: "GET",
        token,
      });
      setResults(data);
    } catch (err) {
      console.error("Í≤ÄÏÉâ Ïã§Ìå®:", err);
      alert("Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  }

  // ÏóîÌÑ∞ÌÇ§Î°ú Í≤ÄÏÉâ Ïã§Ìñâ
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      search();
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>üîç Í≥µÏßÄÏÇ¨Ìï≠ Í≤ÄÏÉâ</h2>
      <input
        type="text"
        value={q}
        onChange={(e) => setQ(e.target.value)}
        onKeyDown={handleKeyPress}
        placeholder="Í≤ÄÏÉâÏñ¥ ÏûÖÎ†• (Ï†úÎ™©/ÎÇ¥Ïö©/ÏûëÏÑ±Ïûê/Î≤îÏúÑ)"
        style={{ width: "300px", marginRight: "10px" }}
      />
      <button onClick={search}>Í≤ÄÏÉâ</button>

      <div style={{ marginTop: "20px" }}>
        {results.length === 0 ? (
          <p>Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
        ) : (
          <ul>
            {results.map((n) => (
              <li key={n.id} style={{ marginBottom: "15px" }}>
                <h4>{n.title}</h4>
                <p>{n.body}</p>
                <small>
                  ÏûëÏÑ±Ïûê: {n.username} / Î≤îÏúÑ: {n.scope} / ÏûëÏÑ±Ïùº:{" "}
                  {new Date(n.created_at).toLocaleString()}
                </small>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
