import React, { useState } from "react";
import { api } from "./api";

export default function Login({ onLogin }) {
  const [u,setU]=useState(""); const [p,setP]=useState(""); const [msg,setMsg]=useState("");
  const submit = async e => {
    e.preventDefault();
    const r = await api("/login",{method:"POST",body:{username:u,password:p}});
    if(r.success){ localStorage.setItem("token",r.token); onLogin(); }
    else setMsg(r.msg||"login failed");
  };
  return (
    <form onSubmit={submit}>
      <input value={u} onChange={e=>setU(e.target.value)} placeholder="username"/>
      <input type="password" value={p} onChange={e=>setP(e.target.value)} placeholder="password"/>
      <button>Login</button>
      <p>{msg}</p>
    </form>
  );
}
