import React, { useState, useEffect } from "react";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { api } from "./api";

// ‚úÖ ÌïúÍµ≠Ïñ¥ Î°úÏª¨ ÏÑ§Ï†ï
const locales = { "ko-KR": require("date-fns/locale/ko") };
const localizer = dateFnsLocalizer({ format, parse, startOfWeek, getDay, locales });

export default function CalendarView({ token, projectId }) {
  const [events, setEvents] = useState([]);   // ÌîÑÎ°úÏ†ùÌä∏ ÏùºÏ†ï
  const [status, setStatus] = useState([]);   // Í∞úÏù∏ ÏÉÅÌÉú (Ìú¥Í∞Ä/Í∑ºÌÉú)
  const [title, setTitle] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [selectedEvent, setSelectedEvent] = useState(null);

  // üìå ÌîÑÎ°úÏ†ùÌä∏ ÏùºÏ†ï Î∂àÎü¨Ïò§Í∏∞
  async function loadEvents() {
    const data = await api(`/events?project_id=${projectId}`, { method: "GET", token });
    return data.map(ev => ({
      id: `event-${ev.id}`,
      type: "event",
      title: ev.title,
      start: new Date(ev.start_date),
      end: new Date(ev.end_date),
    }));
  }

  // üìå Í∞úÏù∏ ÏÉÅÌÉú Î∂àÎü¨Ïò§Í∏∞
  async function loadStatus() {
    const data = await api(`/status`, { method: "GET", token });
    return data.map(s => ({
      id: `status-${s.id}`,
      type: "status",
      title: `[${s.type}] ${s.username}`,
      start: new Date(s.start_date),
      end: new Date(s.end_date),
    }));
  }

  // üìå ÏùºÏ†ï + ÏÉÅÌÉú Ìï©ÏπòÍ∏∞
  async function loadAll() {
    const ev = await loadEvents();
    const st = await loadStatus();
    setEvents([...ev, ...st]);
  }

  useEffect(() => { loadAll(); /* eslint-disable-next-line */ }, []);

  // üìå ÏÉà ÏùºÏ†ï Ï∂îÍ∞Ä
  async function addEvent(e) {
    e.preventDefault();
    if (!title.trim()) return alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!");

    await api("/events", {
      method: "POST",
      token,
      body: {
        project_id: projectId,
        title,
        description: "",
        start_date: formatDateTime(startDate),
        end_date: formatDateTime(endDate),
      },
    });

    setTitle("");
    setStartDate(new Date());
    setEndDate(new Date());
    await loadAll();
  }

  // üìå ÏùºÏ†ï ÏàòÏ†ï
  async function editEvent() {
    if (!selectedEvent || selectedEvent.type !== "event") return alert("ÌîÑÎ°úÏ†ùÌä∏ ÏùºÏ†ïÎßå ÏàòÏ†ï Í∞ÄÎä•Ìï©ÎãàÎã§.");
    const newTitle = prompt("ÏÉà Ï†úÎ™©:", selectedEvent.title);
    if (!newTitle) return;

    await api(`/events/${selectedEvent.id.replace("event-","")}`, {
      method: "PUT",
      token,
      body: {
        title: newTitle,
        start_date: formatDateTime(selectedEvent.start),
        end_date: formatDateTime(selectedEvent.end),
      },
    });

    setSelectedEvent(null);
    await loadAll();
  }

  // üìå ÏùºÏ†ï ÏÇ≠Ï†ú
  async function deleteEvent() {
    if (!selectedEvent) return;
    if (!window.confirm("ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;

    if (selectedEvent.type === "event") {
      await api(`/events/${selectedEvent.id.replace("event-","")}`, { method: "DELETE", token });
    } else if (selectedEvent.type === "status") {
      await api(`/status/${selectedEvent.id.replace("status-","")}`, { method: "DELETE", token });
    }

    setSelectedEvent(null);
    await loadAll();
  }

  return (
    <div style={{ maxWidth: 1000, margin: "0 auto" }}>
      <h2>üìÖ ÌîÑÎ°úÏ†ùÌä∏ {projectId} Ï∫òÎ¶∞Îçî + ÎÇ¥ Í∑ºÌÉú</h2>

      {/* ÏùºÏ†ï Ï∂îÍ∞Ä Ìèº */}
      <form onSubmit={addEvent} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="ÏùºÏ†ï Ï†úÎ™©"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <div style={{ display: "flex", gap: "10px", marginTop: "5px" }}>
          <DatePicker selected={startDate} onChange={setStartDate} showTimeSelect timeFormat="HH:mm" timeIntervals={30} dateFormat="yyyy-MM-dd HH:mm" />
          <DatePicker selected={endDate} onChange={setEndDate} showTimeSelect timeFormat="HH:mm" timeIntervals={30} dateFormat="yyyy-MM-dd HH:mm" />
        </div>
        <button type="submit" style={{ marginTop: "10px" }}>+ ÏùºÏ†ï Ï∂îÍ∞Ä</button>
      </form>

      {/* Ï∫òÎ¶∞Îçî */}
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        views={["month", "week", "day"]}
        defaultView="month"
        messages={{ month: "Ïõî", week: "Ï£º", day: "Ïùº", today: "Ïò§Îäò", previous: "Ïù¥Ï†Ñ", next: "Îã§Ïùå" }}
        onSelectEvent={(event) => setSelectedEvent(event)}
        eventPropGetter={(event) => ({
          style: {
            backgroundColor: event.type === "status" ? "#FFB6C1" : "#4CAF50", // Ìú¥Í∞Ä/Í∑ºÌÉú = ÌïëÌÅ¨, ÌîÑÎ°úÏ†ùÌä∏ = Ï¥àÎ°ù
            color: "black",
          }
        })}
      />

      {/* ÏÑ†ÌÉùÎêú ÏùºÏ†ï Í¥ÄÎ¶¨ */}
      {selectedEvent && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid #ccc" }}>
          <h4>ÏÑ†ÌÉùÎêú Ìï≠Î™©: {selectedEvent.title}</h4>
          {selectedEvent.type === "event" && <button onClick={editEvent}>‚úè ÏàòÏ†ï</button>}
          <button onClick={deleteEvent} style={{ marginLeft: "10px", color: "red" }}>üóë ÏÇ≠Ï†ú</button>
          <button onClick={() => setSelectedEvent(null)} style={{ marginLeft: "10px" }}>‚ùå Ï∑®ÏÜå</button>
        </div>
      )}
    </div>
  );
}

// üìå ÎÇ†Ïßú Ìè¨Îß∑
function formatDateTime(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const h = String(d.getHours()).padStart(2, "0");
  const min = String(d.getMinutes()).padStart(2, "0");
  return `${y}-${m}-${day} ${h}:${min}`;
}
