import React, { useState } from "react";
import { api } from "./api";

export default function NoticeList({ token }) {
  const [items,setItems]=useState([]); const [q,setQ]=useState("");
  const search=async()=>{ setItems(await api(`/notices/search?q=${q}`,{token})); };
  return (
    <div>
      <h3>공지사항</h3>
      <input value={q} onChange={e=>setQ(e.target.value)} placeholder="검색어"/>
      <button onClick={search}>검색</button>
      {items.map(n=>
        <div key={n.id}>
          <b>{n.title}</b><p>{n.body}</p><small>{n.username}</small>
        </div>
      )}
    </div>
  );
}
