import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "./api";

export default function NoticePage({ token }) {
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");
  const navigate = useNavigate();

  // ÏÉà Í≥µÏßÄ ÏûÖÎ†• ÏÉÅÌÉú
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [scope, setScope] = useState("GLOBAL");

  // Ï†ÑÏ≤¥ Í≥µÏßÄ Î∂àÎü¨Ïò§Í∏∞
  async function loadAll() {
    const data = await api("/notices", {
      method: "GET",
      token,
    });
    setItems(data);
  }

  useEffect(() => {
    loadAll();
    // eslint-disable-next-line
  }, []);

  // Í≤ÄÏÉâ Ïã§Ìñâ
  const search = async () => {
    if (!q.trim()) {
      await loadAll(); // Í≤ÄÏÉâÏñ¥ ÏóÜÏúºÎ©¥ Ï†ÑÏ≤¥Î™©Î°ù
      return;
    }
    const data = await api(`/notices/search?q=${encodeURIComponent(q)}`, {
      method: "GET",
      token,
    });
    setItems(data);
  };

  // ÏóîÌÑ∞ÌÇ§ ÏûÖÎ†• Ïãú Í≤ÄÏÉâ Ïã§Ìñâ
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      search();
    }
  };

  // Í≥µÏßÄ ÏûëÏÑ±
  const createNotice = async (e) => {
    e.preventDefault();
    if (!title || !body) {
      alert("Ï†úÎ™©Í≥º ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!");
      return;
    }

    await api("/notices", {
      method: "POST",
      token,
      body: {
        scope,
        title,
        body,
      },
    });

    setTitle("");
    setBody("");
    await loadAll(); // Îì±Î°ù ÌõÑ Ï†ÑÏ≤¥Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
  };

  return (
    <div style={{ maxWidth: "700px", margin: "0 auto" }}>
      <h2>üì¢ Í≥µÏßÄÏÇ¨Ìï≠</h2>

      {/* üîπ Í≥µÏßÄ ÏûëÏÑ± Ìèº */}
      <form onSubmit={createNotice} style={{ marginBottom: "20px" }}>
        <select value={scope} onChange={(e) => setScope(e.target.value)}>
          <option value="GLOBAL">Ï†ÑÏ≤¥</option>
          <option value="TEAM">ÌåÄ</option>
          <option value="PROJECT">ÌîÑÎ°úÏ†ùÌä∏</option>
        </select>
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Ï†úÎ™©"
          required
        />
        <textarea
          value={body}
          onChange={(e) => setBody(e.target.value)}
          placeholder="ÎÇ¥Ïö©"
          required
        />
        <button type="submit">Í≥µÏßÄ Îì±Î°ù</button>
      </form>

      {/* üîé Í≤ÄÏÉâ */}
      <input
        value={q}
        onChange={(e) => setQ(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Í≤ÄÏÉâÏñ¥ ÏûÖÎ†•"
      />
      <button onClick={search}>Í≤ÄÏÉâ</button>

      {/* üìÖ Ï∫òÎ¶∞Îçî Ïù¥Îèô Î≤ÑÌäº */}
      <button
        style={{
          marginLeft: "10px",
          backgroundColor: "#4CAF50",
          color: "white",
        }}
        onClick={() => navigate("/calendar")}
      >
        üìÖ Ï∫òÎ¶∞Îçî Î≥¥Í∏∞
      </button>

      {/* üîπ Í≥µÏßÄ Î™©Î°ù */}
      <div style={{ marginTop: "20px" }}>
        {items.length === 0 ? (
          <p>Îì±Î°ùÎêú Í≥µÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
        ) : (
          items.map((n) => (
            <div
              key={n.id}
              style={{
                marginBottom: "15px",
                padding: "10px",
                borderBottom: "1px solid #ddd",
              }}
            >
              <b>{n.title}</b>
              <p>{n.body}</p>
              <small>
                ÏûëÏÑ±Ïûê: {n.username} [{n.scope}] /{" "}
                {new Date(n.created_at).toLocaleString()}
              </small>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
