import React, { useState, useEffect } from "react";
import { api } from "./api";

export default function StatusBoard({ token }) {
  const [myStatus, setMyStatus] = useState([]);
  const [allStatus, setAllStatus] = useState([]);

  async function load() {
    const mine = await api("/status/me", { method: "GET", token });
    const all = await api("/status", { method: "GET", token });
    setMyStatus(mine);
    setAllStatus(all);
  }

  useEffect(() => { load(); /* eslint-disable */ }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>üë§ ÎÇ¥ Í∑ºÌÉú ÏÉÅÌÉú</h2>
      {myStatus.length === 0 ? (
        <p>Îì±Î°ùÎêú ÏÉÅÌÉúÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {myStatus.map(s => (
            <li key={s.id}>
              {s.status} ({s.start_date} ~ {s.end_date})
            </li>
          ))}
        </ul>
      )}

      <h2 style={{ marginTop: "20px" }}>üë• Ï†ÑÏ≤¥ ÌåÄÏõê ÏÉÅÌÉú</h2>
      {allStatus.length === 0 ? (
        <p>Îì±Î°ùÎêú ÏÉÅÌÉúÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {allStatus.map(s => (
            <li key={s.id}>
              {s.username} - {s.status} ({s.start_date} ~ {s.end_date})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
