import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import DatePicker from "react-datepicker";              
import "react-datepicker/dist/react-datepicker.css";    
import { api } from "./api";

export default function CalendarView({ token, projectId }) {
  const [events, setEvents] = useState([]);
  const [date, setDate] = useState(new Date());

  // ÏÉà ÏùºÏ†ï ÏûÖÎ†• ÏÉÅÌÉú
  const [title, setTitle] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  // ÏùºÏ†ï Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  async function load() {
    const data = await api(`/events?project_id=${projectId}`, {
      method: "GET",
      token,
    });
    setEvents(data);
  }

  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, []);

  // ÏùºÏ†ï Ï∂îÍ∞Ä
  async function addEvent(e) {
    e.preventDefault();
    if (!title) {
      alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!");
      return;
    }

    await api("/events", {
      method: "POST",
      token,
      body: {
        project_id: projectId,
        title,
        description: "",
        start_date: formatDateTime(startDate),
        end_date: formatDateTime(endDate),
      },
    });

    setTitle("");
    setStartDate(new Date());
    setEndDate(new Date());
    await load();
  }

  // ÏÑ†ÌÉùÌïú ÎÇ†ÏßúÏùò Ïù¥Î≤§Ìä∏Îßå ÌïÑÌÑ∞ÎßÅ
  const selected = events.filter(
    (ev) =>
      date &&
      ev.start_date <= formatDate(date) &&
      ev.end_date >= formatDate(date)
  );

  return (
    <div style={{ maxWidth: 720, margin: "0 auto" }}>
      <h2>üìÖ ÌîÑÎ°úÏ†ùÌä∏ {projectId} Ï∫òÎ¶∞Îçî</h2>

      {/* ÏùºÏ†ï Ï∂îÍ∞Ä Ìèº */}
      <form onSubmit={addEvent} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="ÏùºÏ†ï Ï†úÎ™©"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <div style={{ display: "flex", gap: "10px", marginTop: "5px" }}>
          <DatePicker
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={30}
            dateFormat="yyyy-MM-dd HH:mm"
          />
          <DatePicker
            selected={endDate}
            onChange={(date) => setEndDate(date)}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={30}
            dateFormat="yyyy-MM-dd HH:mm"
          />
        </div>
        <button type="submit" style={{ marginTop: "10px" }}>
          + ÏùºÏ†ï Ï∂îÍ∞Ä
        </button>
      </form>

      {/* Îã¨Î†• */}
      <div style={{ marginTop: 12 }}>
        <Calendar
          value={date}
          onChange={(d) => {
            setDate(d);
            setStartDate(new Date(d)); // ÏÑ†ÌÉùÌïú ÎÇ†Ïßú ‚Üí ÏãúÏûëÏùº
            setEndDate(new Date(d));   // ÏÑ†ÌÉùÌïú ÎÇ†Ïßú ‚Üí Ï¢ÖÎ£åÏùº
          }}
        />
      </div>

      <h3 style={{ marginTop: 16 }}>{formatDate(date)} ÏùºÏ†ï</h3>
      {selected.length === 0 ? (
        <p>ÏÑ†ÌÉùÌïú ÎÇ†ÏßúÏóê ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {selected.map((ev) => (
            <li key={ev.id}>
              <b>{ev.title}</b> ({ev.start_date} ~ {ev.end_date})
            </li>
          ))}
        </ul>
      )}

      <h3 style={{ marginTop: 16 }}>Ï†ÑÏ≤¥ ÏùºÏ†ï</h3>
      <ul>
        {events.map((ev) => (
          <li key={ev.id}>
            <b>{ev.title}</b> ({ev.start_date} ~ {ev.end_date})
          </li>
        ))}
      </ul>
    </div>
  );
}

function formatDate(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  return `${y}-${m}-${day}`;
}

function formatDateTime(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const h = String(d.getHours()).padStart(2, "0");
  const min = String(d.getMinutes()).padStart(2, "0");
  return `${y}-${m}-${day} ${h}:${min}`;
}
