import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { api } from "./api";

export default function NoticeDetail({ token }) {
  const { id } = useParams();   // URL의 notice_id
  const [amendments, setAmendments] = useState([]);
  const [newBody, setNewBody] = useState("");

  // 정정 내역 불러오기
  async function loadAmendments() {
    const data = await api(`/notices/${id}/amendments`, { method: "GET", token });
    setAmendments(data);
  }

  // 정정 추가하기
  async function addAmendment(e) {
    e.preventDefault();
    if (!newBody.trim()) return alert("정정 내용을 입력하세요");

    await api(`/notices/${id}/amendments`, {
      method: "POST",
      token,
      body: { body: newBody }
    });
    setNewBody("");
    loadAmendments();
  }

  useEffect(() => {
    loadAmendments();
  }, [id]);

  return (
    <div>
      <h2>공지사항 상세 #{id}</h2>

      <h3>정정 내역</h3>
      <ul>
        {amendments.map(a => (
          <li key={a.id}>
            <b>{a.username}</b>: {a.body} <i>({new Date(a.created_at).toLocaleString()})</i>
          </li>
        ))}
      </ul>

      <form onSubmit={addAmendment}>
        <textarea
          value={newBody}
          onChange={e => setNewBody(e.target.value)}
          placeholder="정정 내용을 입력하세요"
        />
        <button type="submit">정정 추가</button>
      </form>
    </div>
  );
}
