import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Login from "./Login";
import NoticePage from "./NoticePage";   // ‚úÖ ÌÜµÌï©Îêú Í≥µÏßÄ ÌéòÏù¥ÏßÄ
import CalendarView from "./CalendarView";
import ProtectedRoute from "./ProtectedRoute";

function App() {
  const [logged, setLogged] = useState(!!localStorage.getItem("token"));
  const token = localStorage.getItem("token");

  return (
    <BrowserRouter>
      <Routes>
        {/* Î°úÍ∑∏Ïù∏ ÌôîÎ©¥ */}
        <Route
          path="/"
          element={
            <Login
              onLogin={() => {
                setLogged(true);
                // üöÄ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ ÌõÑ Í≥µÏßÄÏÇ¨Ìï≠ÏúºÎ°ú Ïù¥Îèô
                window.history.pushState({}, "", "/notices");
              }}
            />
          }
        />

        {/* Í≥µÏßÄÏÇ¨Ìï≠ (Îì±Î°ù + Í≤ÄÏÉâ + Î™©Î°ù Ìïú ÌôîÎ©¥) */}
        <Route
          path="/notices"
          element={
            <ProtectedRoute>
              <NoticePage token={token} />
            </ProtectedRoute>
          }
        />

        {/* Ï∫òÎ¶∞Îçî */}
        <Route
          path="/calendar"
          element={
            <ProtectedRoute>
              <CalendarView token={token} projectId={1} />
            </ProtectedRoute>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
