// src/App.js
import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Login from "./Login";
import NoticeList from "./NoticeList";
import NoticeBoard from "./NoticeBoard";
import NoticeSearch from "./NoticeSearch";
import CalendarView from "./CalendarView";
import ProtectedRoute from "./ProtectedRoute";
import StatusBoard from "./StatusBoard";
import NoticeDetail from "./NoticeDetail";

function App() {
  const [logged, setLogged] = useState(!!localStorage.getItem("token"));

  return (
    <BrowserRouter>
      <Routes>
        {/* 로그인 화면 */}
        <Route
          path="/"
          element={
            <Login
              onLogin={() => {
                setLogged(true);
                window.location.href = "/notices"; // 로그인 성공 후 이동
              }}
            />
          }
        />

        {/* 공지사항 등록/목록 */}
        <Route
          path="/notices"
          element={
            <ProtectedRoute>
              <NoticeList token={localStorage.getItem("token")} />
            </ProtectedRoute>
          }
        />

        {/* 공지사항 전체 게시판 */}
        <Route
          path="/notice-board"
          element={
            <ProtectedRoute>
              <NoticeBoard token={localStorage.getItem("token")} />
            </ProtectedRoute>
          }
        />

        {/* 공지사항 검색 */}
        <Route
          path="/notice-search"
          element={
            <ProtectedRoute>
              <NoticeSearch token={localStorage.getItem("token")} />
            </ProtectedRoute>
          }
        />

        {/* 공지사항 상세 & 정정 추가 */}
        <Route
          path="/notices/:id"
          element={
            <ProtectedRoute>
              <NoticeDetail token={localStorage.getItem("token")} />
            </ProtectedRoute>
          }
        />

        {/* 캘린더 */}
        <Route
          path="/calendar"
          element={
            <ProtectedRoute>
              <CalendarView
                token={localStorage.getItem("token")}
                projectId={1}
              />
            </ProtectedRoute>
          }
        />

        {/* 상태창 */}
        <Route
          path="/status-board"
          element={
            <ProtectedRoute>
              <StatusBoard token={localStorage.getItem("token")} />
            </ProtectedRoute>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
