import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { api } from "./api";

export default function CalendarView({ token, projectId }) {
  const [events, setEvents] = useState([]);
  const [date, setDate] = useState(new Date());

  // ÏùºÏ†ï Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  async function load() {
    const data = await api(`/events?project_id=${projectId}`, {
      method: "GET",
      token
    });
    setEvents(data);
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, []);

  // ÏùºÏ†ï Ï∂îÍ∞Ä(Í∞ÑÎã® ÌîÑÎ°¨ÌîÑÌä∏)
  async function addEvent() {
    const title = prompt("ÏùºÏ†ï Ï†úÎ™©:");
    const start = prompt("ÏãúÏûëÏùº (YYYY-MM-DD):");
    const end = prompt("Ï¢ÖÎ£åÏùº (YYYY-MM-DD):");
    if (!title || !start || !end) return;

    await api("/events", {
      method: "POST",
      token,
      body: {
        project_id: projectId,
        title,
        description: "",
        start_date: start,
        end_date: end
      }
    });
    await load();
  }

  // ÏÑ†ÌÉùÌïú ÎÇ†ÏßúÏóê Ìï¥ÎãπÌïòÎäî Ïù¥Î≤§Ìä∏Îßå Î≥¥Í∏∞ (Í∞ÑÎã® ÌïÑÌÑ∞)
  const selected = events.filter(
    ev => (date && ev.start_date <= formatDate(date) && ev.end_date >= formatDate(date))
  );

  return (
    <div style={{ maxWidth: 720, margin: "0 auto" }}>
      <h2>üìÖ ÌîÑÎ°úÏ†ùÌä∏ {projectId} Ï∫òÎ¶∞Îçî</h2>
      <button onClick={addEvent}>+ ÏÉà ÏùºÏ†ï Ï∂îÍ∞Ä</button>

      <div style={{ marginTop: 12 }}>
        <Calendar value={date} onChange={setDate} />
      </div>

      <h3 style={{ marginTop: 16 }}>
        {formatDate(date)} ÏùºÏ†ï
      </h3>
      {selected.length === 0 ? (
        <p>ÏÑ†ÌÉùÌïú ÎÇ†ÏßúÏóê ÏùºÏ†ïÏù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {selected.map(ev => (
            <li key={ev.id}>
              <b>{ev.title}</b> ({ev.start_date} ~ {ev.end_date})
            </li>
          ))}
        </ul>
      )}

      <h3 style={{ marginTop: 16 }}>Ï†ÑÏ≤¥ ÏùºÏ†ï</h3>
      <ul>
        {events.map(ev => (
          <li key={ev.id}>
            <b>{ev.title}</b> ({ev.start_date} ~ {ev.end_date})
          </li>
        ))}
      </ul>
    </div>
  );
}

function formatDate(d) {
  // Date -> "YYYY-MM-DD"
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  return `${y}-${m}-${day}`;
}
