import React, { useState, useEffect } from "react";
import { api } from "./api";

export default function NoticeBoard({ token }) {
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");

  // Ï†ÑÏ≤¥ Í≥µÏßÄ Î∂àÎü¨Ïò§Í∏∞
  async function load() {
    try {
      const data = await api("/notices", { method: "GET", token });
      setItems(data);
    } catch (err) {
      console.error("Í≥µÏßÄÏÇ¨Ìï≠ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", err);
    }
  }

  // Í≤ÄÏÉâ Ïã§Ìñâ
  async function search() {
    if (!q.trim()) {
      await load(); // Í≤ÄÏÉâÏñ¥ ÏóÜÏúºÎ©¥ Ï†ÑÏ≤¥Î™©Î°ù
      return;
    }
    try {
      const data = await api(`/notices/search?q=${encodeURIComponent(q)}`, {
        method: "GET",
        token,
      });
      setItems(data);
    } catch (err) {
      console.error("Í≤ÄÏÉâ Ïã§Ìå®:", err);
    }
  }

  // ÏóîÌÑ∞ÌÇ§Î°ú Í≤ÄÏÉâ
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      search();
    }
  };

  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, []);

  return (
    <div style={{ maxWidth: 720, margin: "0 auto" }}>
      <h2>üì¢ Í≥µÏßÄÏÇ¨Ìï≠</h2>

      {/* Í≤ÄÏÉâÏ∞Ω */}
      <div style={{ marginBottom: "16px" }}>
        <input
          value={q}
          onChange={(e) => setQ(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Ï†úÎ™© / ÏûëÏÑ±Ïûê / Î≥∏Î¨∏ / Î≤îÏúÑ(GLOBAL, TEAM, PROJECT)"
          style={{ width: "70%", padding: "8px" }}
        />
        <button onClick={search} style={{ marginLeft: "8px" }}>
          Í≤ÄÏÉâ
        </button>
      </div>

      {/* Í≤∞Í≥º Ï∂úÎ†• */}
      {items.length === 0 ? (
        <p>Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {items.map((n) => (
            <li
              key={n.id}
              style={{
                borderBottom: "1px solid #ddd",
                marginBottom: "12px",
                paddingBottom: "8px",
              }}
            >
              <h4>{n.title}</h4>
              <p>{n.body}</p>
              <small>
                ÏûëÏÑ±Ïûê: {n.username} / Î≤îÏúÑ: {n.scope} / ÏûëÏÑ±Ïùº:{" "}
                {new Date(n.created_at).toLocaleString()}
              </small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
