import React, { useState } from "react";
import { useNavigate } from "react-router-dom";  
import { api } from "./api";

export default function NoticeList({ token }) {
  const [items, setItems] = useState([]); 
  const [q, setQ] = useState("");
  const navigate = useNavigate();

  // 새 공지 입력 상태
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [scope, setScope] = useState("GLOBAL");

  // 검색 실행 함수
  const search = async () => { 
    if (!q.trim()) return; 
    const data = await api(`/notices/search?q=${encodeURIComponent(q)}`, {
      method: "GET",
      token
    });
    setItems(data);
  };

  // 엔터키 입력 시 검색 실행
  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault(); 
      search();
    }
  };

  // 공지 작성
  const createNotice = async (e) => {
    e.preventDefault();
    if (!title || !body) {
      alert("제목과 내용을 입력하세요!");
      return;
    }

    await api("/notices", {
      method: "POST",
      token,
      body: {
        scope,
        title,
        body
      }
    });

    setTitle("");
    setBody("");
    await search(); // 등록 후 목록 갱신
  };

  return (
    <div>
      <h3>공지사항</h3>

      {/* 🔹 공지 작성 폼 */}
      <form onSubmit={createNotice} style={{ marginBottom: "20px" }}>
        <select value={scope} onChange={e => setScope(e.target.value)}>
          <option value="GLOBAL">전체</option>
          <option value="TEAM">팀</option>
          <option value="PROJECT">프로젝트</option>
        </select>
        <input
          value={title}
          onChange={e => setTitle(e.target.value)}
          placeholder="제목"
          required
        />
        <textarea
          value={body}
          onChange={e => setBody(e.target.value)}
          placeholder="내용"
          required
        />
        <button type="submit">공지 등록</button>
      </form>

      {/* 🔎 검색창 */}
      <input
        value={q}
        onChange={e => setQ(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="검색어 입력"
      />
      <button onClick={search}>검색</button>

      {/* 📅 캘린더 이동 버튼 */}
      <button
        style={{ marginLeft: "10px", backgroundColor: "#4CAF50", color: "white" }}
        onClick={() => navigate("/calendar")}
      >
        📅 캘린더 보기
      </button>

      {/* 🔹 검색 결과 목록 */}
      <div style={{ marginTop: "20px" }}>
        {items.map(n =>
          <div key={n.id} style={{ marginBottom: "15px" }}>
            <b>{n.title}</b>
            <p>{n.body}</p>
            <small>작성자: {n.username} [{n.scope}]</small>
          </div>
        )}
      </div>
    </div>
  );
}
