import React, { useState, useEffect } from "react";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { api } from "./api";

// ✅ 한국어 지역 설정
const locales = {
  "ko-KR": require("date-fns/locale/ko"),
};
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

export default function CalendarView({ token, projectId }) {
  const [events, setEvents] = useState([]);
  const [title, setTitle] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  // 선택된 이벤트 (수정/삭제용)
  const [selectedEvent, setSelectedEvent] = useState(null);

  // 📌 일정 불러오기
  async function load() {
    const data = await api(`/events?project_id=${projectId}`, {
      method: "GET",
      token,
    });

    const mapped = data.map((ev) => ({
      id: ev.id,
      title: ev.title,
      start: new Date(ev.start_date),
      end: new Date(ev.end_date),
    }));
    setEvents(mapped);
  }

  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, []);

  // 📌 일정 추가
  async function addEvent(e) {
    e.preventDefault();
    if (!title.trim()) {
      alert("제목을 입력하세요!");
      return;
    }

    await api("/events", {
      method: "POST",
      token,
      body: {
        project_id: projectId,
        title,
        description: "",
        start_date: formatDateTime(startDate),
        end_date: formatDateTime(endDate),
      },
    });

    setTitle("");
    setStartDate(new Date());
    setEndDate(new Date());
    await load();
  }

  // 📌 일정 수정
  async function editEvent() {
    if (!selectedEvent) return;
    const newTitle = prompt("새 제목:", selectedEvent.title);
    if (!newTitle) return;

    await api(`/events/${selectedEvent.id}`, {
      method: "PUT",
      token,
      body: {
        title: newTitle,
        start_date: formatDateTime(selectedEvent.start),
        end_date: formatDateTime(selectedEvent.end),
      },
    });

    setSelectedEvent(null);
    await load();
  }

  // 📌 일정 삭제
  async function deleteEvent() {
    if (!selectedEvent) return;
    if (!window.confirm("삭제하시겠습니까?")) return;

    await api(`/events/${selectedEvent.id}`, {
      method: "DELETE",
      token,
    });

    setSelectedEvent(null);
    await load();
  }

  return (
    <div style={{ maxWidth: 1000, margin: "0 auto" }}>
      <h2>📅 프로젝트 {projectId} 캘린더</h2>

      {/* 일정 추가 폼 */}
      <form onSubmit={addEvent} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="일정 제목"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <div style={{ display: "flex", gap: "10px", marginTop: "5px" }}>
          <DatePicker
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={30}
            dateFormat="yyyy-MM-dd HH:mm"
          />
          <DatePicker
            selected={endDate}
            onChange={(date) => setEndDate(date)}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={30}
            dateFormat="yyyy-MM-dd HH:mm"
          />
        </div>
        <button type="submit" style={{ marginTop: "10px" }}>
          + 일정 추가
        </button>
      </form>

      {/* 달력 */}
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        views={["month", "week", "day"]}
        defaultView="month"
        messages={{
          month: "월",
          week: "주",
          day: "일",
          today: "오늘",
          previous: "이전",
          next: "다음",
        }}
        onSelectEvent={(event) => setSelectedEvent(event)} // ✅ 이벤트 클릭 시 선택
      />

      {/* 선택된 일정 수정/삭제 버튼 */}
      {selectedEvent && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid #ccc" }}>
          <h4>선택된 일정: {selectedEvent.title}</h4>
          <button onClick={editEvent}>✏ 수정</button>
          <button onClick={deleteEvent} style={{ marginLeft: "10px", color: "red" }}>
            🗑 삭제
          </button>
          <button onClick={() => setSelectedEvent(null)} style={{ marginLeft: "10px" }}>
            ❌ 취소
          </button>
        </div>
      )}
    </div>
  );
}

// 📌 날짜 변환 함수
function formatDateTime(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const h = String(d.getHours()).padStart(2, "0");
  const min = String(d.getMinutes()).padStart(2, "0");
  return `${y}-${m}-${day} ${h}:${min}`;
}
