import React, { useState, useEffect } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { api } from "./api";

export default function StatusBoard({ token }) {
  const [items, setItems] = useState([]);
  const [type, setType] = useState("Ìú¥Í∞Ä");
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  // ÏÉÅÌÉú Î∂àÎü¨Ïò§Í∏∞
  async function load() {
    const data = await api("/status", { method: "GET", token });
    setItems(data);
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, []);

  // ÏÉà ÏÉÅÌÉú Îì±Î°ù
  async function addStatus(e) {
    e.preventDefault();
    await api("/status", {
      method: "POST",
      token,
      body: {
        type,
        start_date: formatDateTime(startDate),
        end_date: formatDateTime(endDate),
      },
    });
    await load();
  }

  return (
    <div>
      <h2>üìù ÎÇ¥ Ìú¥Í∞Ä/Í∑ºÌÉú ÏÉÅÌÉú</h2>

      {/* ÏÉÅÌÉú Îì±Î°ù */}
      <form onSubmit={addStatus}>
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="Ìú¥Í∞Ä">Ìú¥Í∞Ä</option>
          <option value="Ï∂úÏû•">Ï∂úÏû•</option>
          <option value="Î≥ëÍ∞Ä">Î≥ëÍ∞Ä</option>
          <option value="Í∏∞ÌÉÄ">Í∏∞ÌÉÄ</option>
        </select>
        <DatePicker selected={startDate} onChange={setStartDate} showTimeSelect dateFormat="yyyy-MM-dd HH:mm" />
        <DatePicker selected={endDate} onChange={setEndDate} showTimeSelect dateFormat="yyyy-MM-dd HH:mm" />
        <button type="submit">+ Îì±Î°ù</button>
      </form>

      {/* Î™©Î°ù */}
      <ul style={{ marginTop: 20 }}>
        {items.map(s => (
          <li key={s.id}>
            <b>{s.type}</b> ({s.start_date} ~ {s.end_date}) - {s.username}
          </li>
        ))}
      </ul>
    </div>
  );
}

function formatDateTime(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const h = String(d.getHours()).padStart(2, "0");
  const min = String(d.getMinutes()).padStart(2, "0");
  return `${y}-${m}-${day} ${h}:${min}`;
}
