import React, { useState, useEffect } from "react";
import { api } from "./api";

export default function NoticeBoard({ token }) {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);

  async function load() {
    const data = await api("/notices", { method: "GET", token });
    setItems(data);
    // JWT ÏïàÏóê user Ï†ïÎ≥¥ Ï†ÄÏû•Îèº ÏûàÏúºÎãà Î°úÏª¨ÏóêÏÑú ÌååÏã±
    const jwtData = JSON.parse(atob(token.split(".")[1]));
    setUser(jwtData);
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, []);

  async function deleteNotice(id) {
    if (!window.confirm("Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;
    await api(`/notices/${id}`, { method: "DELETE", token });
    await load();
  }

  async function editNotice(id, oldTitle, oldBody) {
    const newTitle = prompt("ÏÉà Ï†úÎ™©:", oldTitle);
    const newBody = prompt("ÏÉà ÎÇ¥Ïö©:", oldBody);
    if (!newTitle || !newBody) return;
    await api(`/notices/${id}`, {
      method: "PUT",
      token,
      body: { title: newTitle, body: newBody },
    });
    await load();
  }

  return (
    <div>
      <h2>üì¢ Í≥µÏßÄÏÇ¨Ìï≠ Î™©Î°ù</h2>
      {items.length === 0 ? (
        <p>Í≥µÏßÄÏÇ¨Ìï≠Ïù¥ ÏóÜÏäµÎãàÎã§.</p>
      ) : (
        <ul>
          {items.map((n) => {
            const canEdit =
              user &&
              (
                (n.scope === "GLOBAL" && user.role === "admin") ||
                (n.scope === "TEAM" && user.role === "leader" && user.team_id === n.team_id) ||
                (n.scope === "PROJECT" && user.role === "leader" && user.project_id === n.project_id) ||
                user.id === n.author_id
              );

            return (
              <li key={n.id} style={{ marginBottom: "20px" }}>
                <h4>{n.title}</h4>
                <p>{n.body}</p>
                <small>
                  ÏûëÏÑ±Ïûê: {n.username} / Î≤îÏúÑ: {n.scope} / ÏûëÏÑ±Ïùº:{" "}
                  {new Date(n.created_at).toLocaleString()}
                </small>
                {canEdit && (
                  <div>
                    <button onClick={() => editNotice(n.id, n.title, n.body)}>‚úè ÏàòÏ†ï</button>
                    <button onClick={() => deleteNotice(n.id)}>üóë ÏÇ≠Ï†ú</button>
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
