import React, { useState, useEffect } from "react";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { api } from "./api";

// ‚úÖ ÌïúÍµ≠Ïñ¥ ÏßÄÏó≠ ÏÑ§Ï†ï
const locales = {
  "ko-KR": require("date-fns/locale/ko"),
};
const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

export default function CalendarView({ token, projectId }) {
  const [events, setEvents] = useState([]);
  const [title, setTitle] = useState("");
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());

  // ÏÑ†ÌÉùÎêú Ïù¥Î≤§Ìä∏ (ÏàòÏ†ï/ÏÇ≠Ï†úÏö©)
  const [selectedEvent, setSelectedEvent] = useState(null);

  // üìå ÏùºÏ†ï + Í∞úÏù∏ ÏÉÅÌÉú Î∂àÎü¨Ïò§Í∏∞
  async function load() {
    // ÌîÑÎ°úÏ†ùÌä∏ ÏùºÏ†ï
    const dataEvents = await api(`/events?project_id=${projectId}`, {
      method: "GET",
      token,
    });
    const mappedEvents = dataEvents.map((ev) => ({
      id: `event-${ev.id}`,
      title: ev.title,
      start: new Date(ev.start_date),
      end: new Date(ev.end_date),
      type: "event",
    }));

    // Í∞úÏù∏ ÏÉÅÌÉú
    const dataStatus = await api("/status", { method: "GET", token });
    const mappedStatus = dataStatus.map((st) => ({
      id: `status-${st.id}`,
      title: `${st.username} - ${st.type}`,
      start: new Date(st.start_date),
      end: new Date(st.end_date),
      type: "status",
    }));

    setEvents([...mappedEvents, ...mappedStatus]);
  }

  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, []);

  // üìå ÏùºÏ†ï Ï∂îÍ∞Ä
  async function addEvent(e) {
    e.preventDefault();
    if (!title.trim()) {
      alert("Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî!");
      return;
    }

    await api("/events", {
      method: "POST",
      token,
      body: {
        project_id: projectId,
        title,
        description: "",
        start_date: formatDateTime(startDate),
        end_date: formatDateTime(endDate),
      },
    });

    setTitle("");
    setStartDate(new Date());
    setEndDate(new Date());
    await load();
  }

  // üìå ÏùºÏ†ï ÏàòÏ†ï
  async function editEvent() {
    if (!selectedEvent || selectedEvent.type !== "event") return; // ‚úÖ Í∞úÏù∏ ÏÉÅÌÉúÎäî ÏàòÏ†ï Î∂àÍ∞Ä
    const newTitle = prompt("ÏÉà Ï†úÎ™©:", selectedEvent.title);
    if (!newTitle) return;

    await api(`/events/${selectedEvent.id.replace("event-", "")}`, {
      method: "PUT",
      token,
      body: {
        title: newTitle,
        start_date: formatDateTime(selectedEvent.start),
        end_date: formatDateTime(selectedEvent.end),
      },
    });

    setSelectedEvent(null);
    await load();
  }

  // üìå ÏùºÏ†ï ÏÇ≠Ï†ú
  async function deleteEvent() {
    if (!selectedEvent || selectedEvent.type !== "event") return; // ‚úÖ Í∞úÏù∏ ÏÉÅÌÉúÎäî ÏÇ≠Ï†ú Î∂àÍ∞Ä
    if (!window.confirm("ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;

    await api(`/events/${selectedEvent.id.replace("event-", "")}`, {
      method: "DELETE",
      token,
    });

    setSelectedEvent(null);
    await load();
  }

  return (
    <div style={{ maxWidth: 1000, margin: "0 auto" }}>
      <h2>üìÖ ÌîÑÎ°úÏ†ùÌä∏ {projectId} Ï∫òÎ¶∞Îçî (ÏùºÏ†ï + Í∞úÏù∏ ÏÉÅÌÉú)</h2>

      {/* ÏùºÏ†ï Ï∂îÍ∞Ä Ìèº */}
      <form onSubmit={addEvent} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="ÏùºÏ†ï Ï†úÎ™©"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <div style={{ display: "flex", gap: "10px", marginTop: "5px" }}>
          <DatePicker
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={30}
            dateFormat="yyyy-MM-dd HH:mm"
          />
          <DatePicker
            selected={endDate}
            onChange={(date) => setEndDate(date)}
            showTimeSelect
            timeFormat="HH:mm"
            timeIntervals={30}
            dateFormat="yyyy-MM-dd HH:mm"
          />
        </div>
        <button type="submit" style={{ marginTop: "10px" }}>
          + ÏùºÏ†ï Ï∂îÍ∞Ä
        </button>
      </form>

      {/* Îã¨Î†• */}
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500 }}
        views={["month", "week", "day"]}
        defaultView="month"
        messages={{
          month: "Ïõî",
          week: "Ï£º",
          day: "Ïùº",
          today: "Ïò§Îäò",
          previous: "Ïù¥Ï†Ñ",
          next: "Îã§Ïùå",
        }}
        onSelectEvent={(event) => setSelectedEvent(event)}
        eventPropGetter={(event) => {
          const backgroundColor = event.type === "event" ? "#4cafef" : "#ef5350";
          return { style: { backgroundColor } };
        }}
      />

      {/* ÏÑ†ÌÉùÎêú ÏùºÏ†ï ÏàòÏ†ï/ÏÇ≠Ï†ú Î≤ÑÌäº */}
      {selectedEvent && selectedEvent.type === "event" && (
        <div
          style={{ marginTop: "20px", padding: "10px", border: "1px solid #ccc" }}
        >
          <h4>ÏÑ†ÌÉùÎêú ÏùºÏ†ï: {selectedEvent.title}</h4>
          <button onClick={editEvent}>‚úè ÏàòÏ†ï</button>
          <button
            onClick={deleteEvent}
            style={{ marginLeft: "10px", color: "red" }}
          >
            üóë ÏÇ≠Ï†ú
          </button>
          <button
            onClick={() => setSelectedEvent(null)}
            style={{ marginLeft: "10px" }}
          >
            ‚ùå Ï∑®ÏÜå
          </button>
        </div>
      )}
    </div>
  );
}

// üìå ÎÇ†Ïßú Î≥ÄÌôò Ìï®Ïàò
function formatDateTime(d) {
  const y = d.getFullYear();
  const m = String(d.getMonth() + 1).padStart(2, "0");
  const day = String(d.getDate()).padStart(2, "0");
  const h = String(d.getHours()).padStart(2, "0");
  const min = String(d.getMinutes()).padStart(2, "0");
  return `${y}-${m}-${day} ${h}:${min}`;
}
