import React, { useState, useEffect } from "react";
import { api } from "./api";

export default function NoticeBoard({ token }) {
  const [items, setItems] = useState([]);
  const [user, setUser] = useState(null);

  // 새 공지 작성 상태
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");
  const [scope, setScope] = useState("GLOBAL"); // 기본값 전체공지

  // 공지 불러오기
  async function load() {
    try {
      const data = await api("/notices", {
        method: "GET",
        token,
      });
      setItems(data);
    } catch (err) {
      console.error("공지사항 로드 실패:", err);
    }
  }

  // 현재 로그인 사용자 정보
  async function loadUser() {
    try {
      const data = await api("/me", { method: "GET", token });
      setUser(data);
    } catch (err) {
      console.error("사용자 정보 불러오기 실패:", err);
    }
  }

  useEffect(() => {
    load();
    loadUser();
    // eslint-disable-next-line
  }, []);

  // 공지 작성
  async function addNotice(e) {
    e.preventDefault();
    if (!title.trim() || !body.trim()) {
      alert("제목과 내용을 입력하세요!");
      return;
    }
    try {
      await api("/notices", {
        method: "POST",
        token,
        body: { scope, title, body },
      });
      setTitle("");
      setBody("");
      setScope("GLOBAL");
      await load();
    } catch (err) {
      alert("공지 등록 실패: " + err.message);
    }
  }

  // 공지 삭제
  async function deleteNotice(id) {
    if (!window.confirm("정말 삭제하시겠습니까?")) return;
    try {
      await api(`/notices/${id}`, { method: "DELETE", token });
      await load();
    } catch (err) {
      alert("삭제 실패: " + err.message);
    }
  }

  // 공지 수정
  async function editNotice(id, oldTitle, oldBody) {
    const newTitle = prompt("새 제목:", oldTitle);
    const newBody = prompt("새 내용:", oldBody);
    if (!newTitle || !newBody) return;
    try {
      await api(`/notices/${id}`, {
        method: "PUT",
        token,
        body: { title: newTitle, body: newBody },
      });
      await load();
    } catch (err) {
      alert("수정 실패: " + err.message);
    }
  }

  return (
    <div>
      <h2>📢 공지사항</h2>

      {/* 🔹 공지 작성 폼 */}
      <form onSubmit={addNotice} style={{ marginBottom: "20px" }}>
        <select
          value={scope}
          onChange={(e) => setScope(e.target.value)}
          style={{ marginRight: "10px" }}
        >
          <option value="GLOBAL">전체공지</option>
          <option value="TEAM">팀공지</option>
          <option value="PROJECT">프로젝트공지</option>
        </select>
        <input
          type="text"
          placeholder="제목"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="내용"
          value={body}
          onChange={(e) => setBody(e.target.value)}
          style={{ display: "block", marginTop: "5px", width: "300px", height: "80px" }}
          required
        />
        <button type="submit" style={{ marginTop: "5px" }}>+ 공지 등록</button>
      </form>

      {/* 🔹 공지 목록 */}
      {items.length === 0 ? (
        <p>공지사항이 없습니다.</p>
      ) : (
        <ul>
          {items.map((n) => {
            const canEdit =
              user && (user.role === "admin" || user.username === n.username);
            const canDelete =
              user && (user.role === "admin" || user.username === n.username);

            return (
              <li key={n.id} style={{ marginBottom: "20px" }}>
                <h4>{n.title}</h4>
                <p>{n.body}</p>
                <small>
                  작성자: {n.username} / 범위: {n.scope} / 작성일:{" "}
                  {new Date(n.created_at).toLocaleString()}
                </small>
                <div style={{ marginTop: "8px" }}>
                  {canEdit && (
                    <button
                      onClick={() => editNotice(n.id, n.title, n.body)}
                    >
                      ✏ 수정
                    </button>
                  )}
                  {canDelete && (
                    <button
                      style={{ marginLeft: "8px", color: "red" }}
                      onClick={() => deleteNotice(n.id)}
                    >
                      🗑 삭제
                    </button>
                  )}
                </div>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}
