import React, { useState, useEffect } from "react";
import { api } from "./api";

export default function StatusBoard({ token }) {
  const [items, setItems] = useState([]);
  const [type, setType] = useState("Ìú¥Í∞Ä");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");

  async function load() {
    const data = await api("/status", { method: "GET", token });
    setItems(data);
  }

  async function addStatus(e) {
    e.preventDefault();
    if (!start || !end) return alert("Í∏∞Í∞ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî");
    await api("/status", {
      method: "POST",
      token,
      body: { type, start_date: start, end_date: end }
    });
    setStart(""); setEnd("");
    await load();
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, []);

  return (
    <div>
      <h2>üë§ Í∞úÏù∏ ÏÉÅÌÉúÏ∞Ω</h2>
      <form onSubmit={addStatus}>
        <select value={type} onChange={e => setType(e.target.value)}>
          <option>Ìú¥Í∞Ä</option>
          <option>Ï∂úÏû•</option>
          <option>Î≥ëÍ∞Ä</option>
          <option>Ïû¨ÌÉù</option>
        </select>
        <input type="date" value={start} onChange={e=>setStart(e.target.value)} required/>
        <input type="date" value={end} onChange={e=>setEnd(e.target.value)} required/>
        <button type="submit">Îì±Î°ù</button>
      </form>

      <ul style={{ marginTop: "20px" }}>
        {items.map(s => (
          <li key={s.id}>
            <b>{s.username}</b> - {s.type} ({s.start_date} ~ {s.end_date})
          </li>
        ))}
      </ul>
    </div>
  );
}
